mapper.select:
SELECT boardSeq, writer, title, content, regdate, readCount
FROM tblBoardNotice
WHERE boardSeq = #{seq}

mapper.insert:
INSERT INTO tblBoardNotice (
    boardSeq, writer, title, content, attach
)
VALUES (
    seqBoardNotice.nextVal,
    #{writer}, #{title}, #{content}, #{attach}
)

mapper.update:
UPDATE tblBoardNotice
SET readCount = readCount + 1
WHERE boardSeq = #{seq}

UPDATE tblBoardNotice
SET title = #{title},
    content = #{content}
WHERE boardSeq = #{boardSeq}

mapper.select:

select *
from (
	select a.*, rownum rnum
	from (
		select boardSeq, writer, title, content, regdate, readCount
		from tblBoardNotice
		order by boardSeq desc
	) a
	where rownum <= #{offset} + #{pageSize}
)
where rnum > #{offset}
